def line(x)  line_array = []  if x.length == 0    puts "The line is currently empty."  else    x.each.with_index(1) do |name, index|      line_array.push("#{index}. #{name}")  end    puts "The line is currently: #{line_array.join(" ")}"  endenddef line(katz_deli)  line_array = []  if katz_deli.size == 0    puts "The line is currently empty."  else    line_array.each_with_index do |name, index|      final_array.push("#{index + 1}. #{name}")    end    puts "The line is currently: " + final_array.join(" ")  endend